import React, { useEffect, useState, useRef } from "react";
//import { Radar } from "react-chartjs-2";
import usePlayers from "../../hooks/skrallgaming/usePlayers";
import playersArray from "../../globals/skrallgaming/players"
import placings from "../../globals/skrallgaming/placings"
import "../../assets/style/skrallgaming/createThornament.css"
import { getRoles } from "@testing-library/react";

export default function CreateThornament(){

    const { players, setPlayers } = usePlayers()
    const [ teams, setTeams ] = useState([])

    const [ tournamentName, setTournamentName] = useState('SSE-II 2022 Smash Doubles')
    const [ gameFormat, setGameFormat] = useState('2v2')
    const [ game, setGame] = useState('smash')

    const [ groupStage, setGroupStage ] = useState(true)
    const [ groupMaxTeams, setGroupMaxTeams ] = useState(4)
    const [ groupAdvance, setGroupAdvance ] = useState(2)

    const [ eliminitationType, setEliminitationType ] = useState(2)

    const [ teamName, setTeamName] = useState()
    const [ p1, setP1 ] = useState()
    const [ p2, setP2 ] = useState()

    useState(() => {if (!players?.lenght) setPlayers(playersArray)}, [])

    const registerTeam = () =>{
        if (!p1 || !p2 || !teamName) return
        let arr = [...[], ...teams]
        arr.push({
            name: teamName,
            players: [p1, p2]
        })
        setTeams(arr);
    }

    const removeTeam = (i) => {
        let arr = [...[], ...teams]
        arr.splice(i, 1)
        setTeams(arr)
    }    

    const startThornament = () => {
        if (!teams) return alert("Register teams first!")
        let matches = []
        let uBracketMatches_C = (teams.length - 1)
        let uBracketTeams_C = teams.length;

        if (groupStage){
            let groups = Array(Math.ceil(teams.length / groupMaxTeams))
            let groupId = 0
            for (let i = 0; i < teams.length; i++){
                if (!groups[groupId]) groups[groupId] = []
                groups[groupId] = [...groups?.[groupId], teams[i]]
                groupId++
                if (groupId === groups.length) groupId = 0
            }
            console.log("Groups:", groups)

            groups.map((group, i) => {
                group.map((team, j) => {
                    for(let k = j+1; k < group.length; k++){
                        matches.push({
                            "type":"groupmatch",
                            "group": String.fromCharCode(65+i),
                            "team1": group[j].name,
                            "team2": group[k].name,
                        })
                    }
                })                
            })
            uBracketTeams_C = (groupAdvance * groups.length)
            uBracketMatches_C = uBracketTeams_C - 1
        }

        const getRounds = (int) => {            
            if (typeof int !== "number") return
            let i = 0
            let ret = [];
            
            while (int > 0){
                let rm = Math.pow(2, i)
                if (int >= rm) ret.unshift(rm)
                else ret.unshift(int)
                int -= rm
                i++                
            }

            return ret
        }
        
        if (eliminitationType === 1) 
        uBracketMatches_C += 1 //Bronze Match for single elimination      
        let rounds = getRounds(uBracketMatches_C)
     
        console.log(`Teams left: ${uBracketTeams_C}`)
        console.log(`Upper Bracket Matches left: ${uBracketMatches_C}`)
        console.log("Rounds: ", rounds)

        let upperBracket = []
        rounds.slice(0).reverse().map((r, i) => {
            for(let j = 0; j < r; j++){
                if(uBracketMatches_C === uBracketTeams_C){
                    uBracketMatches_C--;
                    uBracketTeams_C--;
                    
                    if (i === rounds.length - 1){

                    }
                }
            }
        })
    }

    return <>        
        <div className="CreateThornamentBody">
            <div>                
                <span>
                    <fieldset className="part">
                        <legend>Tournament Info</legend>
                        <div className="inputBox">
                            <input id="tournamentName" required 
                                value={tournamentName} 
                                onChange={(e) => setTournamentName(e.target.value)}
                            /> 
                            <span>Tournament Name</span>
                        </div>
                        <div className="inputBox">
                            <select id="gameFormat" value={gameFormat} onChange={(e) => setGameFormat(e.target.value)}>
                                <option value="1v1">1v1</option>
                                <option value="2v2">2v2</option>
                            </select>
                            <span>Game Format</span>
                        </div>
                        <div className="inputBox">
                            <select id="game" value={game} onChange={(e) => setGame(e.target.value)}>
                                <option value="smash">Smash Ultimate</option>
                                <option value="beerPong">Beerpong</option>
                            </select>
                            <span>Game</span>
                        </div>                    
                        <div>
                            <input type="checkbox" id="groupStage" checked={groupStage} onClick={() => setGroupStage(!groupStage)}/>
                            <label htmlFor="groupStage">Groupstage?</label>
                        </div>                    
                        {groupStage &&<>
                            <div className="inputBox">
                                <input type="number" id="maxTeamsPerGrp" required value={groupMaxTeams} onChange={(v) => setGroupMaxTeams(v.target.value)} /> 
                                <span>Max teams per group</span>
                            </div>
                            <div className="inputBox">
                                <input type="number" id="advance" required 
                                    value={groupAdvance} max={groupMaxTeams} min={1}
                                    onChange={(v) => setGroupAdvance(v.target.value)} 
                                /> 
                                <span>Teams advance from each group</span>
                            </div>
                        </>} 
                        <div className="inputBox">
                            <select required id="eliminitationType" defaultValue={eliminitationType} onChange={(v) => setEliminitationType(v.target.value)}>
                                <option value={1}>Single Elimination</option>
                                <option value={2}>Double Elimination</option>
                            </select>
                            <span>Elimination type</span>
                        </div>
                        <div>
                            <input type="checkbox" id="eloStructure"/>
                            <label htmlFor="eloStructure">Structure by Elo</label>
                        </div>                    
                    </fieldset>
                </span>
                <span>
                    <fieldset className="part">
                        <legend>Team Registration</legend>                                            
                        <div className="inputBox">
                            <select id="p1" defaultValue={""} onChange={(e) => setP1(e.target.value)}>
                                <option value="" disabled/>
                                {players &&
                                    players.map(p => <option value={p?.name}>{p?.name}</option>)
                                }
                            </select>
                            <span>Player 1</span>
                        </div>
                        <div className="inputBox">
                            <select id="p2" defaultValue={""} onChange={(e) => setP2(e.target.value)}>
                                <option value="" disabled/>
                                {players &&
                                    players.map(p => <option value={p?.name}>{p?.name}</option>)
                                }
                            </select>
                            <span>Player 2</span>
                        </div>
                        <div className="inputBox">
                            <input id="teamName" required 
                                onChange={(e) => setTeamName(e.target.value)} 
                                onKeyDown={(k) => {if(k.key === "Enter") registerTeam()}} 
                            /> 
                            <span>Team name</span>
                        </div>
                        <button onClick={() => registerTeam()}>Register Team</button>
                    </fieldset>
                </span>
                <span>
                    <fieldset>
                        <legend>Registered Teams</legend>
                        <table className="registeredTeams">
                            <thead><tr>
                                <th></th>
                                <th>Team</th>
                                <th>Players</th>
                            </tr></thead>
                            <tbody>
                                {teams &&
                                    teams.map((t, i) => <tr>
                                        <td>{i+1}</td>
                                        <td>{t.name}</td>
                                        <td>{t.players.join(' & ')}</td>
                                        <td><i onClick={() => removeTeam(i)}>‚ùå</i></td>
                                    </tr>)
                                }
                            </tbody>
                        </table>
                    </fieldset>
                </span>
            </div>            
            {
                teams &&
                <div className="finishButton" onClick={() => startThornament()}>Hold to start Thornament</div>
            }
        </div>    
    </>
}
