import React, { useEffect, useState, useRef } from "react";
//import { Radar } from "react-chartjs-2";
import usePlayers from "../../hooks/skrallgaming/usePlayers";
import playersArray from "../../globals/skrallgaming/players"
import placings from "../../globals/skrallgaming/placings"
import "../../assets/style/skrallgaming/createThornament.css"
import { getRoles } from "@testing-library/react";
import { useHref } from "react-router-dom";

export default function CreateThornament(){

    const { players, setPlayers } = usePlayers()
    const [ teams, setTeams ] = useState([])

    const tournamentName = useRef('SSE-II 2022 Smash Doubles')
    const gameFormat = useRef('2v2')
    const game = useRef('smash')

    const groupStage = useRef(true)
    const groupMaxTeams  = useRef(4)
    const groupAdvance = useRef(2)

    const eliminitationType = useRef(2)

    const teamName = useRef()
    const p1 = useRef()
    const p2 = useRef()

    useState(() => {if (!players?.lenght) setPlayers(playersArray)}, [])

    const registerTeam = () =>{
        if (!p1.current.value || !p2.current.value || !teamName.current.value) return
        let arr = [...[], ...teams]
        arr.push({
            name: teamName.current.value,
            players: [p1.current.value, p2.current.value]
        })
        setTeams(arr);
    }

    const removeTeam = (i) => {
        let arr = [...[], ...teams]
        arr.splice(i, 1)
        setTeams(arr)
    }    

    const startThornament = () => {
        if (!teams) return alert("Register teams first!")
        let matches = []
        let uBracketMatches_C = (teams.length - 1)
        let uBracketTeams_C = teams.length;
        let groups = []

        if (groupStage.current.value){
            groups = Array(Math.ceil(teams.length / groupMaxTeams.current.value))
            let groupId = 0
            for (let i = 0; i < teams.length; i++){
                if (!groups[groupId]) groups[groupId] = []
                groups[groupId] = [...groups?.[groupId], teams[i]]
                groupId++
                if (groupId === groups.length) groupId = 0
            }
            console.log("Groups:", groups)

            groups.map((group, i) => {
                group.map((team, j) => {
                    for(let k = j+1; k < group.length; k++){
                        matches.push({
                            "type":"groupmatch",
                            "group": String.fromCharCode(65+i),
                            "team1": group[j].name,
                            "team2": group[k].name,
                        })
                    }
                })                
            })
            uBracketTeams_C = (groupAdvance.current.value * groups.length)
            uBracketMatches_C = uBracketTeams_C - 1
        }
        if (eliminitationType.current.value === 1) uBracketMatches_C += 1 //Bronze Match for single elimination

        const getRounds = (int) => {            
            if (typeof int !== "number") return
            let i = 0
            let ret = [];
            
            while (int > 0){
                let rm = Math.pow(2, i)
                if (int >= rm) ret.unshift(rm)
                else ret.unshift(int)
                int -= rm
                i++                
            }

            return ret
        }
        
        const getOpponent = (id, type) => {
            if (id > 1) return id-1            
            return (type === "group" ? groups.length: groupAdvance.current.value)
        }

        let rounds = getRounds(uBracketMatches_C)
        let groupId = groups.length
        let advanceId = groupAdvance.current.value

        const getRestMatches = (arr) => {
            // Hitta matchen som ska substitueras med Referense
            // 
            if (arr === undefined) arr = Array(rounds[1])
            let arr2 = arr.splice(arr.length/2)
            let rest_c = uBracketTeams_C / 2 - rounds[0]

        }                
     
        console.log(`Teams left: ${uBracketTeams_C}`)
        console.log(`Upper Bracket Matches left: ${uBracketMatches_C}`)
        console.log("Rounds: ", rounds)

        let upperBracket = []
        rounds.map((r, i) => {
            if (i>0)return
            for(let j = 0; j < r; j++){
                if(i === 0){                    
                    upperBracket.push({
                        "team1": {group: groupId, placement: advanceId},
                        "team2": {group: getOpponent(groupId, "group"), placement: getOpponent(advanceId, "placement")},
                        "round": i,
                        "match": upperBracket.length+1
                    })
                }
                groupId -= 1
                advanceId -= 1
                uBracketMatches_C--;
