import React, { useEffect, useState } from "react";
//import { Radar } from "react-chartjs-2";
import usePlayers from "../../hooks/skrallgaming/usePlayers";
import playersArray from "../../globals/skrallgaming/players"
import placings from "../../globals/skrallgaming/placings"
import "../../assets/style/skrallgaming/thornament.css"

export default function Thornament(){

    const teams = {
        "Fizzle My Nizzle": ["Mikka", "R"],
        "Bramers": ["Acrid", "Lexclusive"],
        "NextGen": ["Barre", "YOLOwille"],
        "IDK": ["Zeor", "Finch"],
        "Deltalux": ["Eb0la", "Avvan"],
        "Raindrops": ["J√§ger", "Jarmo"],
    }

    const matches = [
        ["Fizzle My Nizzle", "Bramers"],
        ["Fizzle My Nizzle", "NextGen"],
        ["Bramers", "NextGen"],
        ["IDK", "Deltalux"],
        ["Raindrops", "Bramers"],
        ["IDK", "Fizzle My Nizzle"],
    ]
    
    const teamColor = (teamName) => {
        // let hash = 0;
        // for (let i = 0; i < teamName.length; i++) {
        //     hash = teamName.charCodeAt(i) + ((hash << 5) - hash);
        //     hash = hash & hash;
        // }
        // return hash % 360
        let teamsArr = Object.keys(teams)
        let c = teamsArr.findIndex(t => t === teamName)
        return Math.round(c / teamsArr.length * 360)
    }

    return <>        
        <div className="body">
            <div className="ulkeeper">
                <ul>
                    {
                        matches.map((match, index) => {
                            console.log(match[0], teamColor(match[0]), match[1], teamColor(match[1]))
                            return <li onClick={() => alert("tja")}>
                                    <span className="matchcount">MATCH {index+1}</span>
                                    <div className="teambody" style={{
                                        background: `hsl(${teamColor(match[0])}, 100%, 24%)`,
                                        boxShadow: `inset 12px 12px 12px hsl(${teamColor(match[0])}, 100%, 15%), inset -12px -12px 12px hsl(${teamColor(match[0])}, 100%, 29%)`,
                                    }}>
                                        <div className="teamplayers">
                                            {
                                                teams[match[0]].map(p => {                                                    
                                                    return <p>{p}</p>
                                                })
                                                //teams[match[0]].map(p => {console.log(p)})
                                            }
                                        </div>
                                        <div className="teamname ">{match[0]}</div>
                                    </div>
                                    <div className="scorebody">
                                        <div className="score input"></div>
                                        <div className="score input"></div>
                                        <div className="score input"></div> 
                                            {/* {(index === 0 ? '1 - 2': index === 1 ? 'üî•': '‚è≥')} */}                                        
                                    </div>
                                    <div className="teambody" style={{
                                        background: `hsl(${teamColor(match[1])}, 100%, 24%)`,
                                        boxShadow: `inset 12px 12px 12px hsl(${teamColor(match[1])}, 100%, 15%), inset -12px -12px 12px hsl(${teamColor(match[1])}, 100%, 29%)`,
                                    }}>
                                        <div className="teamname">{match[1]}</div>
                                        <div className="teamplayers">
                                            {
                                                teams[match[1]].map(p => {
                                                    return <p>{p}</p>
                                                })
                                                //teams[match[0]].map(p => {console.log(p)})
                                            }
                                        </div>
                                    </div>           
                            </li>
                        })
                    }
                </ul>
            </div>
        </div>
    </>
}
